library(shiny)

nasal_cavity <- 0.5
thyroid_cartilage <- 0.5
glottis <- 0.5
lips <- 0.5
teeth <- 0.5
alveolar <- 0.5
velum_uvula <- 1
palate <- 0.5
epiglottis <- 0.5
tongue <- 1

ui <- fluidPage(

    # Application title
    titlePanel("Speech-Organs"),

    sidebarLayout(
        sidebarPanel(
    
            verbatimTextOutput("")
        ),

        # Show a plot of the generated distribution
        # Create a spot for the barplot
        mainPanel(
            tabsetPanel(
                tabPanel("Place of Articulation", uiOutput("svgout"))
            )
            ,p("")
        )
    )
)

